import "../../../css/Promotions.css";

const PromotionBox = () => {
  const options = ["q", "r", "b", "n"];

  return (
    <div className="promotionBox">
      {options.map((opt) => (
        <div className="piece">{opt}</div>
      ))}
    </div>
  );
};

export default PromotionBox;
